{
  "width": 300,
  "height": 300,
  "autosize": {
    "contains": "padding",
    "resize": true,
    "type": "pad"
  },
  "signals": [
    {
      "name": "active_name",
      "value": null,
      "on": [
       {
          "events": "*:mouseover",
          "update": "datum.category"
        },
        {
          "update": "null",
          "events": "*:mouseout"
        }
      ]
    }
  ],
  "data": [
    {
      "name": "widget_datapoints_consumption",
      "urlTemplate": "/api/v1/widget_datapoints/${region.gid_1}/eng_cons_totl/",
      "format": {
        "property": "widget_datapoints",
        "type": "json",
        "parse": {
          "month_date": "date:'%Y-%m-%d'"
        }
      },
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["gid_2","year"],
          "fields": ["eng_cons_totl"],
          "ops": ["max"],
          "as": ["value"]
        },
        {
          "type": "formula", 
          "as": "category", 
          "expr": "'Consumption'"
        }
      ]
    },
    {
      "name": "widget_datapoints_production",
      "urlTemplate": "/api/v1/widget_datapoints/${region.gid_1}/eng_prod_totl/",
      "format": {
        "property": "widget_datapoints",
        "type": "json",
        "parse": {
          "month_date": "date:'%Y-%m-%d'"
        }
      },
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["gid_2","year"],
          "fields": ["eng_prod_totl"],
          "ops": ["max"],
          "as": ["value"]
        },
        {
          "type": "formula", 
          "as": "category", 
          "expr": "'Production'"
        }
      ]
    },
    {"name": "widget_datapoints",
     "source" : ["widget_datapoints_consumption", "widget_datapoints_production"]
    }
  ],
  "scales": [
    {
      "name": "color",
      "type": "ordinal",
      "range": ["#85B65B", "#427487"],
      "domain": {"data": "widget_datapoints", "field": "category"}
    },
    {
      "name": "year",
      "type": "linear",
      "range": "width",
      "zero": false,
      "nice": false,
      "domain": {
        "data": "widget_datapoints",
        "field": "year"
      }
    },
    {
      "name": "energy",
      "type": "linear",
      "range": "height",
      "zero": true,
      "nice": true,
      "domain": {
        "data": "widget_datapoints",
        "field": "value"
      }
    }
  ],
  "axes": [
    {
      "labelOverlap": "parity",
      "scale": "year",
      "format": "4f",
      "tickMinStep":1,
      "zindex": 1,
      "orient": "bottom"
    },
    {
      "title": "Energy (quadrillion Btu)",
      "scale": "energy",
      "labelOverlap": "parity",
      "format": "f",
      "grid": true,
      "zindex": 0,
      "orient": "left"
    }
  ],
  "marks": [
    {
      "type": "group",
      "from": {
        "facet": {
          "name": "series",
          "data": "widget_datapoints",
          "groupby": "category"
        }
      },
    "marks": [
    {
      "name": "lines",
      "from": {"data": "series"},
      "interactive": false,
      "type": "line",
      "encode": {
        "enter": {
          "y": {
            "field": "value",
            "scale": "energy"
          },
          "x": {
            "field": "year",
            "scale": "year"
          },
          "stroke": {
             "scale": "color", 
             "field": "category" 
          },
          "strokeWidth": {"value": 3}
        }
      }
    },
    {
          "type": "symbol",
          "from": {
            "data": "series"
          },
          "encode": {
            "enter": {
              "x": {
                "scale": "year",
                "field": "year"
              },
              "y": {
                "scale": "energy",
                "field": "value"
              },
              "stroke": {
                "scale": "color",
                "field": "category"
              },
              "strokeWidth": {
                "value": 1.5
              },
              "size": {
                "signal": "width/4"
              }
            },
            "update": {
              "fill": {
                "value": "white"
              },
              "fillOpacity": {
                "value": 0
              },
              "strokeOpacity": {
                "signal": "datum.category == active_name ? 1.0 : 0.0"
              }
            },
            "hover": {
              "fill": {
                "scale": "color",
                "field": "category"
              },
              "fillOpacity": {
                "value": 1
              },
              "tooltip": {
                  "signal": "{'Type': 'Energy '+ datum.category, 'Value': format(datum.value, '.2f') + ' quadrillion Btu', 'Date': datum.year}"
          }
            }
          }
        }
  ]
}
],
"legends": [
    {
      "type": "symbol",
      "direction": "horizontal",
      "orient": "bottom",
      "stroke": "color",
      "fill": "color",
      "fillOpacity": 0.2,
      "labelFont": "Lato"
    }
  ]
}

