{
  "width": 300,
  "height": 300,
  "autosize": {
    "contains": "padding",
    "resize": true,
    "type": "pad"
  },
  "signals": [
    {
      "name": "hover",
      "value": null,
      "on": [
        {
          "update": "datum",
          "events": "@cell:mouseover"
        },
        {
          "update": "null",
          "events": "@cell:mouseout"
        }
      ]
    }
  ],
  "data": [
    {
      "name": "widget_datapoints",
      "urlTemplate": "/api/v1/widget_datapoints/${region.gid_1}/EG-ELC-ACCS-ZS/",
      "format": {
        "property": "widget_datapoints",
        "type": "json",
        "parse": {
          "month_indep": "date:'%Y-%m-%d'"
        }
      },
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["gid_2","year"],
          "fields": ["EG-ELC-ACCS-ZS"],
          "ops": ["max"],
          "as": ["value"]
        }
      ]
    },
    {
      "name": "dots",
      "source": "widget_datapoints",
      "transform": [
        {
          "expr": "hover && hover.datum.year === datum.year",
          "type": "filter"
        }
      ]
    }
  ],
  "scales": [
    {
      "name": "year",
      "type": "linear",
      "range": "width",
      "zero": false,
      "nice": false,
      "domain": {
        "data": "widget_datapoints",
        "field": "year"
      }
    },
    {
      "name": "month_indep",
      "type": "time",
      "range": "width",
      "domain": {
        "data": "widget_datapoints",
        "field": "month_indep"
      }
    },
    {
      "name": "x_scale",
      "type": "linear",
      "range": "height",
      "zero": true,
      "nice": true,
      "domain": {
        "data": "widget_datapoints",
        "field": "value"
      }
    }
  ],
  "axes": [
    {
      "labelOverlap": "parity",
      "scale": "year",
      "format": "4f",
      "tickMinStep": 1,
      "zindex": 1,
      "orient": "bottom"
    },
    {
      "title": "Access to Electricity (% of Population)",
      "scale": "x_scale",
      "labelOverlap": "parity",
      "grid": true,
      "zindex": 0,
      "orient": "left",
      "encode": {
          "labels": {
              "update": {
                  "text":{"signal": "toString(datum.value) + '%'"}
               }
           }
       }
    }
  ],
  "marks": [
    {
      "name": "lines",
      "interactive": false,
      "type": "line",
      "encode": {
