{
  "$schema": "https://vega.github.io/schema/vega/v5.json",
  "width": 300,
  "height": 300,
  "autosize": {
    "type": "none",
    "resize": true,
    "contains": "padding"
  },
  "signals": [
    {"name": "center", "update": "width / 2"}
  ],
  "data": [
    {
      "name": "widget_datapoints",
      "urlTemplate": "/api/v1/widget_datapoints/${region.gid_1}/bws/",
      "format": {
        "property": "widget_datapoints",
        "type": "json",
        "parse": {
          "month_indep": "date:'%Y-%m-%d'"
        }
      },
      "transform": [
        {
          "type": "aggregate",
          "fields": ["gid_2", "bws"],
          "ops": ["max", "max"],
          "as": ["max_month", "max_bws"]
        },
        {
          "type": "formula",
          "as": "formatted_value",
          "expr": "format(datum.max_bws, '.2f')"
        },
        {
          "type": "formula",
          "as": "label",
          "expr": "(datum.max_bws<1?'Low':(datum.max_bws<2?'Low-medium':(datum.max_bws<3?'Medium-high':(datum.max_bws<4?'High':'Extremely high'))))"
        }
      ]
    }
  ],
  "encode": {
    "enter": {
      "x": {"signal": "center"},
      "y": {"signal": "center"}
    }
  },

  "marks": [
    {
      "type": "text",
      "name": "my-text",
      "zindex": 1,
      "encode": {
        "enter": {
          "x": {"signal": "0"},
          "y": {"signal": "40"},
          "text": {"value": "Baseline water stress"},
          "align": {"value": "center"},
          "baseline": {"value": "middle"},
          "fill": {"value": "black"},
          "fontWeight": {"value": "bold"},
          "fontSize": {"value": 20},
           "fill": {
             "value": "#2e3348"
           }
        }
      }
    },
    {
      "type": "text",
      "name": "my-label",
      "from": {"data": "widget_datapoints"},
      "zindex": 1,
      "encode": {
        "enter": {
          "x": {"signal": "0"},
          "y": {"signal": "-20"},
          "text": {"field": "label"},
          "align": {"value": "center"},
          "baseline": {"value": "middle"},
          "fill": {"value": "black"},
          "fontWeight": {"value": "bold"},
          "fontSize": {"value": 40},
           "fill": {
             "value": "#2e3348"
           }
        }
      }
    },
    {
      "type": "text",
      "name": "my-value",
      "from": {"data": "widget_datapoints"},
      "zindex": 1,
      "encode": {
        "enter": {
          "x": {"signal": "0"},
          "y": {"signal": "-80"},
          "text": {"field": "formatted_value"},
          "align": {"value": "center"},
          "baseline": {"value": "middle"},
          "fill": {"value": "black"},
          "fontWeight": {"value": "bold"},
          "fontSize": {"value": 45},
           "fill": {
             "value": "#2e3348"
           }
        }
      }
    }
  ]
}

