{
    "width": 300,
    "height": 300,
    "autosize": {
      "contains": "padding",
      "resize": true,
      "type": "pad"
    },
    "signals": [
      {
        "name": "hover",
        "value": null,
        "on": [
          {
            "update": "datum",
            "events": "@cell:mouseover"
          },
          {
            "update": "null",
            "events": "@cell:mouseout"
          }
        ]
      }
    ],
    "data": [
      {
        "name": "widget_datapoints",
        "urlTemplate": "/api/v1/widget_datapoints/${region.gid_1}/al_ethnic/",
        "format": {
          "property": "widget_datapoints",
          "type": "json",
          "parse": {
            "month_indep": "date:'%Y-%m-%d'"
          }
        },
        "transform": [
          {
            "type": "aggregate",
            "groupby": ["gid_2","year"],
            "fields": ["al_ethnic"],
            "ops": ["max"],
            "as": ["ratio"]
          }
        ]
      },
      {
        "name": "dots",
        "source": "widget_datapoints",
        "transform": [
          {
            "expr": "hover && hover.datum.year === datum.year",
            "type": "filter"
          }
        ]
      }
    ],
    "scales": [
      {
        "name": "year",
        "type": "linear",
        "range": "width",
        "zero": false,
        "nice": false,
        "domain": {
          "data": "widget_datapoints",
          "field": "year"
        }
      },
      {
        "name": "month_indep",
        "type": "time",
        "range": "width",
        "domain": {
          "data": "widget_datapoints",
          "field": "month_indep"
        }
      },
      {
        "name": "y_axis",
        "type": "linear",
        "range": "height",
        "zero": true,
        "nice": true,
        "domain": {
          "data": "widget_datapoints",
          "field": "ratio"
        }
      }
    ],
    "axes": [
      {
        "labelOverlap": true,
        "labelSeparation":4,
        "scale": "year",
        "format": "4f",
         "tickMinStep": 1,
        "zindex": 1,
        "orient": "bottom"
      },
      {
        "title": "Fractionalization",
        "scale": "y_axis",
        "labelOverlap": "parity",
        "grid": true,
        "zindex": 0,
        "orient": "left"
      }
    ],
    "marks": [
      {
        "name": "lines",
        "interactive": false,
        "type": "line",
        "encode": {
          "enter": {
            "y": {
              "field": "ratio",
              "scale": "y_axis"
            },
            "x": {
              "field": "year",
              "scale": "year"
            },
            "stroke": {
             "value": "#673320"
           },
           "strokeWidth": {
            "value": 3
          }
          }
        },
        "from": {
          "data": "widget_datapoints"
        }
      },
      {
        "name": "points",
        "type": "symbol",
        "interactive": false,
        "encode": {
          "update": {
            "opacity": {
              "value": 1
            }
          },
          "enter": {
            "y": {
              "field": "ratio",
              "scale": "y_axis"
            },
            "x": {
              "field": "year",
              "scale": "year"
            },
            "fill": {
             "value": "#673320"
           }
          }
        },
        "from": {
          "data": "dots"
        }
      },
      {
        "name": "cell",
        "type": "path",
        "encode": {
          "update": {
            "opacity": {
              "value": 0
            },
            "path": {
              "field": "path"
            },
            "fill": {
              "value": "red"
            }
          },
          "hover": {
            "tooltip": {
              "signal": "{'Fractionalization': format(hover.datum['ratio'], '.3f'), 'Year': hover.datum.year}"
            }
          }
        },
        "transform": [
          {
            "size": [
              {
                "signal": "width"
              },
              {
                "signal": "height"
              }
            ],
            "y": "datum.y",
            "x": "datum.x",
            "type": "voronoi"
          }
        ],
        "from": {
          "data": "lines"
        }
      }
    ]
  }

