        "property": "widget_datapoints",
        "type": "json",
        "parse": {
          "month_indep": "date:'%Y-%m-%d'"
        }
      },
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["gid_2"],
          "fields": ["rainfed"],
          "ops": ["max"],
          "as": ["Rainfed"]
        },
        {"type": "formula", 
        "as": "Irrigated", 
        "expr": "1-datum.Rainfed"},
        {"type": "fold", 
        "fields": ["Rainfed", "Irrigated"],
        "as": ["category","value_pie"]},
        {
          "type": "pie",
          "field": "value_pie",
          "sort": false
        }
      ]
    }
  ],
"scales": [
    {
      "name": "color",
      "type": "ordinal",
      "domain": {"data": "widget_datapoints", "field": "category"},
      "range": ["#437387","#673320"]
    }
  ],
  "marks": [
    {
      "type": "arc",
      "from": {"data": "widget_datapoints"},
      "encode": {
        "enter": {
          "fill": {"scale": "color", "field": "category"},
          "x": {"signal": "width/ 2"},
          "y": {"signal": "height/ 2"}
        },
        "update": {
          "startAngle": {"field": "startAngle"},
          "endAngle": {"field": "endAngle"},
          "padAngle": {"value": 0},
          "innerRadius": {"value": 0},
          "outerRadius": {"signal": "width / 3"},
          "cornerRadius": {"value": 0},
          "stroke": {"value": "white"},
          "strokeWidth": {"value": 0},
          "fillOpacity": {"value": 0.9}
