    "title": {
      "enter": {
        "fill": {"value": "#2e3348"}
      }
    }
  }
},
  "legends": [
    {
      "orient": "bottom",
      "direction": "horizontal",
      "type": "symbol",
      "fill": "color",
      "offset": {"value": -20},
      "encode": {
        "labels": {
