{
  "width": 300,
  "height": 300,
  "autosize": {
    "contains": "padding",
    "resize": true,
    "type": "pad"
  },
  "signals": [
    {
      "name": "hover",
      "value": null,
      "on": [
        {
          "update": "datum",
          "events": "@cell:mouseover"
        },
        {
          "update": "null",
          "events": "@cell:mouseout"
        }
      ]
    }
  ],
  "data": [
    {
      "name": "widget_datapoints",
      "urlTemplate": "/api/v1/widget_datapoints/${region.gid_1}/DeliveredkcalFraction/",
      "format": {
        "property": "widget_datapoints",
        "type": "json",
        "parse": {
          "month_indep": "date:'%Y-%m-%d'"
        }
      },
      "transform": [
        {
          "type": "aggregate",
          "groupby": ["gid_2"],
          "fields": ["DeliveredkcalFraction"],
          "ops": ["mean"],
          "as": ["Food"]
        },
        {"type": "formula", 
        "as": "Feed and Nonfood", 
        "expr": "1-datum.Food"},
        {"type": "fold", 
        "fields": ["Food", "Feed and Nonfood"],
        "as": ["category","value_pie"]},
        {
          "type": "pie",
          "field": "value_pie",
          "sort": false
        }
      ]
    }
  ],
"scales": [
    {
      "name": "color",
      "type": "ordinal",
      "domain": {"data": "widget_datapoints", "field": "category"},
      "range": ["#437387","#673320"]
    }
  ],
  "marks": [
    {
      "type": "arc",
      "from": {"data": "widget_datapoints"},
      "encode": {
        "enter": {
          "fill": {"scale": "color", "field": "category"},
          "x": {"signal": "width/ 2"},
          "y": {"signal": "height/ 2"}
        },
        "update": {
          "startAngle": {"field": "startAngle"},
          "endAngle": {"field": "endAngle"},
          "padAngle": {"value": 0},
          "innerRadius": {"value": 0},
          "outerRadius": {"signal": "width / 3"},
          "cornerRadius": {"value": 0},
          "stroke": {"value": "white"},
          "strokeWidth": {"value": 0},
          "fillOpacity": {"value": 0.9}
        },
        "hover": {
          "fillOpacity": {"value": 0.7},
          "zindex": {"value": 0},
          "tooltip": {
            "signal": "{'Allocation': datum.category, 'Fraction of calories': format(datum.value_pie, '.3f')}"
          }
        }
      }
    }
  ],
  "title": {
  "text": "Crop Allocation to Food, Feed, and Nonfood in 2000", 
  "color": {"value": "#2e3348"},
  "offset": {"value": -20},
  "encode": {
    "title": {
      "enter": {
        "fill": {"value": "#2e3348"}
      }
    }
  }
},
  "legends": [
    {
      "orient": "bottom",
      "direction": "horizontal",
      "type": "symbol",
      "fill": "color",
      "offset": {"value": -20},
      "encode": {
        "labels": {
          "enter": {
            "fill": {"value": "#2e3348"}
      }
    }
  }
    }
  ]
}

