    "type": "none",
    "resize": true,
    "contains": "padding"
  },
  "signals": [
    {"name": "center", "update": "width / 2"}
  ],
  "data": [
    {
      "name": "widget_datapoints",
      "urlTemplate": "/api/v1/widget_datapoints/${region.gid_1}/yield_gap_barley/",
      "format": {
        "property": "widget_datapoints",
        "type": "json",
        "parse": {
          "month_indep": "date:'%Y-%m-%d'"
        }
      },
      "transform": [
        {
          "type": "formula",
          "as": "formatted",
          "expr": "format(datum.yield_gap_barley,',.2f')"
        }
      ]
    }
  ],
  "encode": {
    "enter": {
      "x": {"signal": "center"},
      "y": {"signal": "center"}
    }
  },

  "marks": [
    {
      "type": "text",
      "name": "my-label",
      "zindex": 1,
      "encode": {
        "enter": {
          "x": {"signal": "0"},
          "y": {"signal": "40"},
          "text": {"value": "average barley yield gap"},
          "align": {"value": "center"},
          "baseline": {"value": "middle"},
          "fill": {"value": "#2e3348"},
          "fontWeight": {"value": "bold"},
          "fontSize": {"value": 20}
        }
      }
    },{
      "type": "text",
      "name": "my-units",
      "zindex": 1,
      "encode": {
        "enter": {
          "x": {"signal": "0"},
          "y": {"signal": "70"},
          "text": {"value": "(1997-2003)"},
          "align": {"value": "center"},
          "baseline": {"value": "middle"},
          "fill": {"value": "#2e3348"},
          "fontWeight": {"value": "normal"},
          "fontSize": {"value": 18}
        }
      }
    },
    {
      "type": "text",
      "name": "my-value",
      "from": {"data": "widget_datapoints"},
      "zindex": 1,
      "encode": {
        "enter": {
          "x": {"signal": "0"},
          "y": {"signal": "-20"},
          "text": {"field": "formatted"},
          "align": {"value": "center"},
          "baseline": {"value": "middle"},
          "fill": {"value": "#2e3348"},
          "fontWeight": {"value": "bold"},
          "fontSize": {"value": 70}
        }
      }
    }
  ]
}

